    var utc= localTime + offset;
    showdate= new Date(utc + 1000 * city.timezone)
    setHours(showdate.toTimeString())
    setDates(showdate.toDateString())
},1000)

},[])
return (
    <div>
      {hours}<br/>
      {dates}
    </div>
    );
}
