import React, { useState,useEffect } from "react";
import List from "./List";
const api=`https://api.openweathermap.org/data/2.5/weather?q=London&appid=fa3dd4e1d5753bebb65b815b24918780&units=metric`;
const api2=`https://api.openweathermap.org/data/2.5/weather?`;
const Form= ()=> {
    const [city,setCity]=useState([]);
    const [find,setFind]=useState("");



const handlerEvent= async (e)=>{    
    e.preventDefault();
        const wheather= await fetch (`${api2}q=${find}&appid=fa3dd4e1d5753bebb65b815b24918780&units=metric`);
        const wheatherJson= await wheather.json();
        const weatherFinal= wheatherJson.main;
       setCity([weatherFinal]);
}


    return (
      <div className="form">
       <div>
           <form onSubmit={handlerEvent}>
               <input type="text" name="city" onChange={e=>setFind(e.target.value)} placeholder="Ingrese su Ciudad"/>
             <button> Agregar</button>
             {
                   city.map(g=>console.log(g.temp))
                }
           </form>
           
       </div>
     
      </div>
    );
  }
  
  export default Form;
