import React from "react";
import styles from "./List.module.css";
import Datetime from "./Datetime";
import{ FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import{ faCloud, faCloudMoonRain, faSun } from "@fortawesome/free-solid-svg-icons";
const List= ({city, temp, winds, cloud,clima})=> {
const showdate= new Date();
console.log(showdate)
const s=showdate.getHours();

var condicion;
    if(city){
        if(clima[0].description==="few clouds"){
         condicion=<FontAwesomeIcon className={styles.icono} icon={faCloud}/>
        }else if(clima[0].description==="broken clouds"){
         condicion=<FontAwesomeIcon className={styles.icono} icon={faCloudMoonRain}/>
        }
        else if(clima[0].description==="clear sky" ){
            condicion=<FontAwesomeIcon className={styles.icono} icon={faSun}/>
           }
    }
    return (
    <div className={styles.container}>
        <div className={styles.hijo}>
            <div>
                <h1>{city.name}</h1>
                <Datetime />
            </div>
            <h2> {city? clima[0].description:""}</h2>
            <h2><FontAwesomeIcon className={styles.icono} icon={cloud.all>0? faCloud: faSun}/>
                </h2>
            <div>
                <div>
                    <h1>{temp.temp}ยบ</h1>
                    <h3>Feels like: {temp.feels_like}ยบ</h3>
                    <h6>Min: {temp.temp_min}ยบ</h6>
                    <h6>Max: {temp.temp_max}ยบ</h6>
                </div>
              <div>
                  {winds.speed}
              </div>
            </div>        
            <div>
                Humidity: <h5>{temp.humidity} </h5>
            </div>
          <h2> condicion={condicion}</h2>
        </div>     
    </div>
    )

   



}
  export default List;
