import { map } from "mathjs";
import React, { useState,useEffect } from "react";
import List from "./List";
const api=`https://api.openweathermap.org/data/2.5/weather?q=London&appid=fa3dd4e1d5753bebb65b815b24918780&units=metric`;
const api2=`https://api.openweathermap.org/data/2.5/weather?`;
const Form= ()=> {
    const [city,setcity]=useState([]);
    const [find,setFind]=useState("");



useEffect(()=>{
    const data= async()=>{
        const wheater= await fetch (api);
        const wheaterJson= await wheater.json();
        const weatherFinal= wheaterJson.main;
        setcity(weatherFinal)
    }    
    data()
},[]);

const handlerEvent= async (e)=>{    
    e.preventDefault();
        const wheater= await fetch (`${api2}q=${find}&appid=fa3dd4e1d5753bebb65b815b24918780&units=metric`);
        const wheaterJson= await wheater.json();
        const weatherFinal= wheaterJson.main;
       setcity(weatherFinal);
}


    return (
      <div className="form">
       <div>
           <form onSubmit={handlerEvent}>
               <input type="text" name="city" onChange={e=>setFind(e.target.value)} placeholder="Ingrese su Ciudad"/>
               <button > Buscar</button>
           </form>
       </div>
       {
           city.length>0? city.map(general=><List key={general.id} general={general} />): console.log(city)
       }
      </div>
    );
  }
  
  export default Form;
